void mainImage( out vec4 fragColor, in vec2 fragCoord )
{
    // Normalized pixel coordinates (from 0 to 1)
    vec2 uv = (fragCoord * 2.0 - iResolution.xy)/iResolution.y;

    uv -= sin(iTime);


    vec3 col = vec3(.1, .2, .3);

    float d = length(uv) + sin(.1*iTime);

    d = 4./d;


    col *= d;


    // Output to screen
    fragColor = vec4(col,1.0);
}
